import React, { useEffect, useState } from 'react'
import './styles/style.css'
import useInterval from './useInterval'

function App() {
  let obstacleCount = 6
  const [obstacle, setObstacle] = useState([])
  const [dummyState, setDummyState] = useState(1)
  const [playerCoordinates, setPlayerCoordinates] = useState([[50,0],[50,5],[50,10],[50,15],[50,20]])
  const [direction, setDirection] = useState()

  //Read up down left and right keys and map to state
  document.onkeydown = (e) => {
    e = e || window.event;
    if (e.keyCode === 38) {
      setDirection("UP")
    } else if (e.keyCode === 40) {
      setDirection("DOWN")
    } else if (e.keyCode === 37) {
      setDirection("LEFT")
    } else if (e.keyCode === 39) {
      setDirection("RIGHT")
    }
  }
  






  function moveObstacles(){
      let tempStore = obstacle
      for (let i = 0; i<tempStore.length; i++){
        tempStore[i][1] = tempStore[i][1]-(obstacleCount-1)
      }
      setObstacle(tempStore)
      setDummyState((item)=>{
        return item-1
      })
    }

  function generateObstacles(){
    let tempStore = obstacle
    const random = Math.random()*50
    let height = (Math.floor(random/10)*10)+10

    if(tempStore.length<obstacleCount){      
      tempStore.push([height,0])
      }
    else{
      tempStore.push([height,(100-(obstacleCount-1))])
      tempStore.shift()
    }
    setObstacle(tempStore)
    setDummyState((item)=>{
      return item-1
    })
  }

  useInterval(moveObstacles,1000)
  useInterval(generateObstacles,4000 )

  const obstacleView = obstacle.map((item)=>{
    let top = 100 -item[0]
    const obstacleStyle = {
      height: `${item[0]}%`,
      top: `${top}%`,
      left: `${item[1]}%`
    }
    return( <div className="obstacle" style={obstacleStyle}></div>)
  })

  const playerView = playerCoordinates.map((item)=>{
    const playerStyle = {
      top: `${item[0]}%`,
      left: `${item[1]}%` 
    }
    return(<div className="player" style={playerStyle}></div>)
  })

  return (
    <div className="canvas">
      {obstacleView}
      {playerView}
    </div>
  )
}

export default App
