import React, { useState } from 'react'
import './styles/style.css'
import useInterval from './useInterval'

function App() {
  const [obstacle, setObstacle] = useState([[10,95],[45,75],[20,55]])
  const [left, setLeft] = useState([95])

  function moveObstacles(){
      let tempStore = obstacle
      for (let i = 0; i<tempStore.length; i++){
        tempStore[i][1] = tempStore[i][1]-5
      }
      
      setObstacle(tempStore)
      console.log(tempStore)
      console.log(obstacle)
    }


  function generateObstacles(){
    let height = Math.floor(Math.random()*50)
    setObstacle((item)=>{
      return ([...item,height])
    })
    console.log("Obstacle generated")
  }

  useInterval(moveObstacles,5000)
  //useInterval(generateObstacles,5000)

  const obstacleView = obstacle.map((item)=>{
    let top = 100 -item[0]
    const obstacleStyle = {
      height: `${item[0]}%`,
      top: `${top}%`,
      left: `${item[1]}%`
    }
    return( <div className="obstacle" style={obstacleStyle}></div>)
  })

  console.log(obstacle)
  return (
    <div className="canvas">
        {obstacleView}
    </div>
  )
}

export default App
