import React, { useEffect, useState } from 'react'
import './styles/style.css'
import LowerObstacles from './components/LowerObstacles'
import UpperObstacles from './components/UpperObstacles'
import Player from './components/Player'
import Score from './components/Score'
import useInterval from './useInterval'

//var playerCoordinates = []

function App() {
    const [playerCoordinates,setPlayerCoordinates] = useState() 
    const [count, setCount] = useState(0)
    const [gameOver,setGameOver] = useState(false)
    function timed(){
      
      setCount((item)=> item+1)
     // console.log(count)
    }
    useInterval(timed,1000)

    const getPlayerCoordinates =(coordinates)=>{
      //console.log(""+coordinates);
      //playerCoordinates = coordinates
      setPlayerCoordinates(coordinates)
    }

    const getLowerObstaclesCoordinates =(coordinates)=>{
      for(let i=0; i<coordinates.length; i++){
        //console.log(""+coordinates[i][1]);
        if(playerCoordinates[0]== undefined){}
        else{
          //console.log(""+playerCoordinates[0][1])
          if(playerCoordinates[0][1]== coordinates[i][1] && (100-playerCoordinates[0][0])<=coordinates[i][0]){
             console.log("Collision") 
            }
        }
      } 
    }

    const getUpperObstaclesCoordinates =(coordinates)=>{
      for(let i=0; i<coordinates.length; i++){
        //console.log(""+coordinates[i][1]);
        if(playerCoordinates[0]== undefined){}
        else{
          //console.log(""+playerCoordinates[0][1])
          if(playerCoordinates[0][1]== coordinates[i][1] && playerCoordinates[0][0]<coordinates[i][0]){
            // console.log("Collision")
          }
        }
      }
    }

    const gameView = 
     (<div className="canvas">
      <LowerObstacles onUpdate={getLowerObstaclesCoordinates}/>
      <Player onPlayerMove={getPlayerCoordinates}/>
      </div>
     )
    
    const gameOverView =(<div className="gameOver">
      <h1>Game Over</h1>
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 256 256"><rect width="56" height="56" fill="none"/><circle cx="128" cy="128" r="96" fill="none" stroke="#000" stroke-linecap="round" stroke-linejoin="round" stroke-width="24"/><circle cx="92" cy="108" r="16"/><circle cx="164" cy="108" r="16"/><path d="M165.3,169.8a48,48,0,0,0-74.6,0" fill="none" stroke="#000" stroke-linecap="round" stroke-linejoin="round" stroke-width="24"/></svg>
      </div>)
    return (
    <div className="main">
      {gameOver? gameView : gameOverView}
    <Score score={count}/>
    </div>
  )
}

export default App
