import React, { useState } from 'react'
import './styles/style.css'
import useInterval from './useInterval'

function App() {
  const [obstacle, setObstacle] = useState([[10,100],[45,80],[20,60],[60,40],[5,20]])
  const [left, setLeft] = useState([95])

  function moveObstacles(){
      let tempStore = obstacle
      for (let i = 0; i<tempStore.length; i++){
        tempStore[i][1] = tempStore[i][1]-5
      }

      setObstacle(tempStore)
      setLeft((item)=>{
        return item-1
      })
    }

    console.log("Rerendered")

  function generateObstacles(){
    let tempStore = obstacle
    let height = Math.floor(Math.random()*70)

    if(tempStore.length<6){
      
      tempStore.push([height,0])
    }
    
    else{
        
        tempStore.push([height,95])
        tempStore.shift()
    }
    
    setObstacle(tempStore)
    console.log(obstacle)
    setLeft((item)=>{
      return item-1
    })
  }

  
  useInterval(moveObstacles,1000)
  useInterval(generateObstacles,4000)

  const obstacleView = obstacle.map((item)=>{
    let top = 100 -item[0]
    const obstacleStyle = {
      height: `${item[0]}%`,
      top: `${top}%`,
      left: `${item[1]}%`
    }
    return( <div className="obstacle" style={obstacleStyle}></div>)
  })

 // console.log(obstacle)
  return (
    <div className="canvas">
        {obstacleView}
    </div>
  )
}

export default App
