import React, { useEffect, useState } from 'react'
import '../styles/style.css'
import useInterval from '../useInterval'
import GridItem from './GridItem'


function UpperObstacles(props) {
    let obstacleCount = 5
    const [currentSpeed, setCurrentSpeed] = useState(400)
    const [obstacle, setObstacle] = useState([])
    
    const updateSpeed = ()=>{
        setCurrentSpeed((item)=> item-10)
    }

    useInterval(updateSpeed,1000)

    function moveObstacles(){
        let tempStore = Array.from(obstacle)
        for (let i = 0; i<tempStore.length; i++){
          tempStore[i][1] = tempStore[i][1]-(obstacleCount)
        }
        setObstacle(tempStore)
      }
  
    function generateObstacles(){
      let tempStore = Array.from(obstacle)
      const random = Math.random()*50
      let height = (Math.floor(random/10)*10)+20
  
      if(tempStore.length<=obstacleCount){      
        tempStore.push([height,95])
       
      }
      else{
        tempStore.push([height,(100-(obstacleCount))])
        tempStore.shift()
      }
      setObstacle(tempStore)
    }

  
    useInterval(moveObstacles,currentSpeed)
    useInterval(generateObstacles,currentSpeed*4)

    props.onUpdate(obstacle)

    const obstacleView = obstacle.map((item)=>{
      let color
      let bottom = 100 -item[0]
      const obstacleStyle = {
        height: `${item[0]}%`,
        bottom: `${bottom}%`,
        left: `${item[1]}%`
      }

      switch(item[0]){
        case 20:
            color= "#87FF3C"
            break;
        case 30:
          color = "#FF55EE"
          break;
        case 40:
          color= "#4332FF"
          break;
        case 50:
          color = "#00FF91"
          break;
        case 60:
          color = "#FF002F"
          break;
           
      }

      const gridStyle = {
        backgroundColor: color 
      }

      return( <div className="obstacle" style={obstacleStyle}><GridItem style={gridStyle}/></div>)
    })
  
     // console.log("Up: ",obstacle)
    return (
    <div>
        {obstacleView}
    </div>
  )
}

export default UpperObstacles
