import { defaultCell } from "./Cell"

export const BuildBoard = ({rows, columns}) =>{
    const builtRows = Array.from({length: rows}, ()=>
        Array.from({length: columns}, ()=>({...defaultCell}))
    )

    return{
        rows: builtRows,
        size: {rows, columns}
    }
}
