import React, { useEffect, useState } from 'react'
import useInterval from '../useInterval'
import '../styles/style.css'

function Player(props) {
    const [playerCoordinates, setPlayerCoordinates] = useState([[50,20]])
    // const [playerCoordinates, setPlayerCoordinates] = useState([[50,0],[50,5],[50,10],[50,15],[50,20]])
    const [direction, setDirection] = useState()

    //Read up down left and right keys and map to state
    document.onkeydown = (e) => {
      e = e || window.event;
      var tempPlayerCoordinates = Array.from(playerCoordinates)
      if (e.keyCode === 38) {
        for (let i = 0; i < tempPlayerCoordinates.length; i++) {
          tempPlayerCoordinates[i][0] = tempPlayerCoordinates[i][0]-10 
        }
        //Up
      } else if (e.keyCode === 40) {
        for (let i = 0; i < tempPlayerCoordinates.length; i++) {
          tempPlayerCoordinates[i][0] = tempPlayerCoordinates[i][0]+10 
        }
        //Down
      } else if (e.keyCode === 37) {
        for (let i = 0; i < tempPlayerCoordinates.length; i++) {
          tempPlayerCoordinates[i][1] = tempPlayerCoordinates[i][1]-5 
        }
        
        //Left
      } else if (e.keyCode === 39) {
        for (let i = 0; i < tempPlayerCoordinates.length; i++) {
          tempPlayerCoordinates[i][1] = tempPlayerCoordinates[i][1]+5 
        }
        
        //Right
      }
      
      setPlayerCoordinates(tempPlayerCoordinates)
    }
    
    console.log(props.onPlayerMove())
   
    const playerView = playerCoordinates.map((item)=>{
      const playerStyle = {
        top: `${item[0]}%`,
        left: `${item[1]}%` 
      }
      return(<div className="player" style={playerStyle}></div>)
    })
  
    return (
    <div>{playerView}</div>
  )
}

export default Player
