import React, { useEffect, useState } from 'react'
import '../styles/style.css'
import useInterval from '../useInterval'
import GridItem from './GridItem'

let count = 0

function LowerObstacles() {
    let currentSpeed = 700
    let obstacleCount = 5
    const [obstacle, setObstacle] = useState([])
    
    function moveObstacles(){
        let tempStore = Array.from(obstacle)
        for (let i = 0; i<tempStore.length; i++){
          tempStore[i][1] = tempStore[i][1]-(obstacleCount)
        }
        setObstacle(tempStore)
        
      }
  
    function generateObstacles(){
      let tempStore = Array.from(obstacle)
      const random = Math.random()*30
      let height = (Math.floor(random/10)*10)+30
  
      if(tempStore.length<=obstacleCount){      
        tempStore.push([height,105])
        }
      else{
        tempStore.push([height,(100+(obstacleCount))])
        tempStore.shift()
      }
      setObstacle(tempStore)
      count = count+1
    }
    
    useInterval(moveObstacles,currentSpeed)
    useInterval(generateObstacles,currentSpeed*4) 
    //At the 4 second it does both moving and generating hence the component gets rendered twice
  
    const obstacleView = obstacle.map((item)=>{
      let top = 100 -item[0]
      const obstacleStyle = {
        height: `${item[0]}%`,
        top: `${top}%`,
        left: `${item[1]}%`
      }
      return( <div className="obstacle" style={obstacleStyle}><GridItem/></div>)
    })
    
  
    return (
    <div>
        {obstacleView}
    </div>
  )
}

export default LowerObstacles
